<script>
    jQuery(document).ready(function() { 
        if(window.isDesktop === true) {
            $(window).on('scroll', function(){
                parallax('#fluid-tablet', 0.6, 'up');
                //bgParallax('#fluid-hero', 0.4, 'down');
                //bgParallax('#fluid-app-icon', 0.15, 'down');
                //scrollDarken('#fluid-hero .color-overlay');

                var fluidFixedTrigger = $('#fluid-fixed-trigger').offset().top;

                if((window.scrollY+window.innerHeight) > fluidFixedTrigger) {
                    $('#fluid-final').removeClass('fixed');
                }
                else if ((window.scrollY) < fluidFixedTrigger) {
                    $('#fluid-final').addClass('fixed');
                }

                //process slides fixer
                var $processTitle = $('#fluid-process-title'),
                    $topPos = $processTitle.parent().offset().top;
console.log(window.scrollY, $topPos);
                    if(window.scrollY >= $topPos) {
                        $processTitle.addClass('fixed');
                    }
                    else if (window.scrollY < $topPos) {
                        $processTitle.removeClass('fixed');
                    }

                    if(!$('#fluid-final').hasClass('fixed')) {

                        var $moveUp = -(window.scrollY - $('#fluid-final').offset().top);
                        $processTitle.css('margin-top', $moveUp);
                    }

                    else if($('#fluid-final').hasClass('fixed')) {
                      $processTitle.css('margin-top', 0);
                    }



            });
            
        }

    });
    </script>
    

